            libraryList = list
        }
        fun getList(kind:String): ArrayList<Library>? {
            var list = ArrayList<Library>()
            if (libraryList != null){
                for(i in libraryList!!){
                    if(i.kind == kind){
                        list.add(i)
                    }
                }
            }
            return list
        }
    }
